										width:40px;
										line-height:38px;
										border-radius: 0px 5px 5px 0px;
										-moz-border-radius: 0px 5px 5px 0px;
										-webkit-border-radius: 0px 5px 5px 0px;
										font-size:12px;
										font-weight:700;
										color:#999;
										cursor: pointer;
										background:#fff;
									   }

.minimal-light .esg-cartbutton {
								color:#333;
								cursor: default !important;
								}
.minimal-light .esg-cartbutton .esgicon-basket {color:#333;
												font-size:15px;
												line-height:15px;
												margin-right:10px;
												}
.minimal-light  .esg-cartbutton-wrapper { cursor: default !important; }

.minimal-light .esg-sortbutton,
.minimal-light .esg-cartbutton { display:inline-block;
								position:relative;
								cursor: pointer;
								margin-right:0px;
								border-right:none;
								border-radius:5px 0px 0px 5px;
								-moz-border-radius:5px 0px 0px 5px;
								-webkit-border-radius:5px 0px 0px 5px;
								}

.minimal-light .esg-navigationbutton:hover,
.minimal-light .esg-filterbutton:hover,
.minimal-light .esg-sortbutton:hover,
.minimal-light .esg-sortbutton-order:hover,
.minimal-light .esg-cartbutton a:hover,
.minimal-light .esg-filterbutton.selected {
											background-color:#fff;
											border-color:#bbb;
											color:#333;
											box-shadow: 0px 3px 5px 0px rgba(0,0,0,0.13);
										  }

.minimal-light .esg-navigationbutton:hover * { color:#333; }

.minimal-light .esg-sortbutton-order.tp-desc:hover {
													border-color:#bbb;
													color:#333;
													box-shadow: 0px -3px 5px 0px rgba(0,0,0,0.13) !important;
