navigation-skins.css:
/********************************
	-	FLAT LIGHT BUTTONS -
*********************************/
.stash-filters .navigationbuttons,
.stash-filters .esg-pagination,
.stash-filters .esg-filters{	text-transform:uppercase;
							text-align: center;
						}

.stash-filters .esg-filterbutton,
.stash-filters .esg-navigationbutton,
.stash-filters .esg-sortbutton,
.stash-filters .esg-cartbutton {	color:#c0c0c0;
								margin-right:5px;
								cursor:pointer;
								position: relative;
								z-index:2;
								padding:1px 15px;
								border:none;
								line-height:18px;
								font-size:12px;
								font-weight:500;
								font-family:"Montserrat",sans-serif;
								display: inline-block;
								margin-bottom:5px;
							}

.stash-filters .esg-navigationbutton	{ padding:2px 6px; }
.stash-filters .esg-navigationbutton *	{ color:#000; }
.stash-filters .esg-pagination-button:last-child { margin-right: 0; }

.stash-filters  .esg-sortbutton-wrapper,
.stash-filters  .esg-cartbutton-wrapper { display:inline-block; }
.stash-filters  .esg-sortbutton-order,
.stash-filters  .esg-cartbutton-order {	display:inline-block;
										vertical-align:top;
										border:none;
										width:40px;
										line-height:40px;
										border-radius:5px;
										-moz-border-radius:5px;
										-webkit-border-radius:5px;
										font-size:12px;
										font-weight:700;
										color:#999;
										cursor: pointer;
										background:#eee;
										background: #fff;
										margin-left:5px;
									}

.stash-filters .esg-cartbutton {	color:#fff;
								cursor: default !important;
							}
.stash-filters .esg-cartbutton .esgicon-basket	{
												color:#fff;
												font-size:15px;
												line-height:15px;
												margin-right:10px;
											}
.stash-filters .esg-cartbutton-wrapper { cursor: default !important; }

.stash-filters .esg-sortbutton,
.stash-filters .esg-cartbutton {	display:inline-block;
								position:relative;
								cursor: pointer;
								margin-right:0px;
								border-radius:5px;
								-moz-border-radius:5px;
								-webkit-border-radius:5px;
							}

.stash-filters .esg-navigationbutton:hover,
.stash-filters .esg-filterbutton:hover,
.stash-filters .esg-sortbutton:hover,
.stash-filters .esg-sortbutton-order:hover,
.stash-filters .esg-cartbutton-order:hover,
.stash-filters .esg-filterbutton.selected {
										border-color:none;color:#000;
										background:#fff;
									   }

.stash-filters .esg-navigationbutton:hover * { color:#333; }

.stash-filters .esg-sortbutton-order.tp-desc:hover	{ color:#333; }

.stash-filters .esg-filter-checked	{	padding:1px 3px;
									color:#cbcbcb;
									background:#cbcbcb;
									margin-left:7px;
									font-size:9px;
									font-weight:300;
									line-height:9px;
									vertical-align: middle;
								}
.stash-filters .esg-filterbutton.selected .esg-filter-checked,
.stash-filters .esg-filterbutton:hover .esg-filter-checked	{
															padding:1px 3px 1px 3px;
															color:#fff;
															background:#000;
															margin-left:7px;
															font-size:9px;
															font-weight:300;
															line-height:9px;
															vertical-align: middle;
														}

